import React, { Component } from 'react';
import {
  StyleSheet,
  TabBarIOS,
  View
} from 'react-native';

import Teaching from './ios_views/Teaching';
import Homework from './ios_views/Homework';
import TheClass from './ios_views/TheClass';
import My from './ios_views/My';

let teaching_base64 = 'data:image/png;base64,'
let homework_base64 = 'data:image/png;base64,'
let theClass_base64 = 'data:image/png;base64,'
let my_base64 = 'data:image/png;base64,';


export default class TabBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 'teaching'
    }
  }

  _renderView(){
    let view = null;
    switch (this.state.selectedTab){
      case 'teaching':
        view = <Teaching></Teaching>;
        break;
      case 'homework':
        view = <Homework></Homework>;
        break;
      case 'TheClass':
        view = <TheClass></TheClass>
        break;
      case 'my':
        view = <My></My>
    }
    return view;
  }

  render() {
    return (
      <TabBarIOS>
        <TabBarIOS.Item
          title="教学"
          icon={{uri: teaching_base64, scale:3.5}}
          selected={this.state.selectedTab === 'teaching'}
          onPress={() => {
            this.setState({
              selectedTab: 'teaching',
            });
          }}>
          {this._renderView()}
        </TabBarIOS.Item>
        <TabBarIOS.Item
          title='作业'
          icon={{uri: homework_base64, scale:3.5}}
          selected={this.state.selectedTab === 'homework'}
          onPress={() => {
            this.setState({
              selectedTab: 'homework'
            });
          }}>
          {this._renderView()}
        </TabBarIOS.Item>
        <TabBarIOS.Item
          title='班级'
          icon={{uri: theClass_base64, scale:3.5}}
          selected={this.state.selectedTab === 'TheClass'}
          onPress={() => {
            this.setState({
              selectedTab: 'TheClass'
            });
          }}>
          {this._renderView()}
        </TabBarIOS.Item>
        <TabBarIOS.Item
          title='我的'
          icon={{uri: my_base64, scale:3.5}}
          selected={this.state.selectedTab === 'my'}
          onPress={() => {
            this.setState({
              selectedTab: 'my'
            });
          }}>
          {this._renderView()}
        </TabBarIOS.Item>
      </TabBarIOS>
    )
  }

}

const styles = StyleSheet.create({
  tabContent: {
    flex: 1,
  }
});
